            int maxV = 0;
            int faceIndex = 0;
            foreach (var face in faces)
            {
                if (face.v1 < minV)
                    minV = face.v1;
                if (face.v2 < minV)
                    minV = face.v2;
                if (face.v3 < minV)
                    minV = face.v3;

                if (face.v1 > maxV)
                    maxV = face.v1;
                if (face.v2 > maxV)
                    maxV = face.v2;
                if (face.v3 > maxV)
                    maxV = face.v3;


                string script = " new THREE.Face3(" + face.v1 + ", " + face.v2 + ", " + face.v3 + "),";
