                ColorScript += faceColorScript;
                faceIndex++;
            }
            ViewBag.ColorScript = ColorScript;
            ViewBag.VerticesScript = VerticesScript.Substring(0, VerticesScript.Length - 1);
            ViewBag.FacesScript = FacesScript.Substring(0, FacesScript.Length - 1); ;
            ViewBag.NumberOfVertices = vertices.Count();
            ViewBag.MinV = minV;
            ViewBag.MaxV = maxV;
            return View();
        }
        private string getColor(int colorNumber)
        {
            if (colorNumber == 1)
                return "0xff0000";
            else if (colorNumber == 2)
                return "0x00ff00";
            else
                return "0x0000ff";
        }
